package Payroll; import java.util.*; import java.util.function.*; public class CompanyPayroll{ ArrayList<Employee> 𝓬; public CompanyPayroll(ArrayList<Employee> 𝒆){ ((Consumer<ArrayList<Employee>>)(x->this.𝓬=x)).accept(𝒆); } public CompanyPayroll(){ this.𝓬=new ArrayList<>(); } public void addEmployee(Employee 𝜏){ ((Consumer<Employee>)(x->this.𝓬.add(x))).accept(𝜏); } public double[] 𝜙(){ double[] 𝛼=new double[this.𝓬.size()]; for(int 𝜉=0;(𝜉<this.𝓬.size())?true:false;𝜉++){ 𝛼[𝜉]=this.𝓬.get(𝜉).getWeeklyPay(); } return 𝛼; } public double[] calculatePay(){return 𝜙();} }
